function AjaxErrorHandle(jqXHR, exception) {
    var msg = '';

    if (jqXHR.status === 0) {
        msg = 'Not connect.\n Verify Network.';
    } else if (jqXHR.status == 404) {
        msg = 'Requested page not found. [404]';
    } else if (jqXHR.status == 500) {
        msg = 'Internal Server Error [500].';
    } else if (exception === 'parsererror') {
        msg = 'Requested JSON parse failed.';
    } else if (exception === 'timeout') {
        msg = 'Time out error.';
    } else if (exception === 'abort') {
        msg = 'Ajax request aborted.';
    } else {
        msg = 'Uncaught Error.\n' + jqXHR.responseText;
    }

    return msg;
}

function AppDateTimeFormat(strDateTime) {
    var date_time = Date.parse(strDateTime);
    var ConvertedDate = moment(date_time).format("lll"); //moment(date_time).format("ll"); //Sep 27, 2016
    return ConvertedDate;
}

function Notification(type, title, msg) {
    toastr.options = {
        "closeButton": true,
        "debug": false,
        "newestOnTop": true,
        "progressBar": true,
        "positionClass": "toast-top-right", //toast-bottom-full-width
        "preventDuplicates": 1,
        "preventOpenDuplicates": 1,
        "onclick": null,
        "showDuration": "300",
        "hideDuration": "1000",
        "timeOut": "5000",
        "extendedTimeOut": "1000",
        "showEasing": "swing",
        "hideEasing": "linear",
        "showMethod": "fadeIn",
        "hideMethod": "fadeOut"
    };

    switch (type) {
        case "success":
            toastr.success(msg, title, toastr.options);
            break;

        case "info":
            toastr.info(msg, title, toastr.options);
            break;

        case "warning":
            toastr.warning(msg, title, toastr.options);
            break;

        case "error":
        case "danger":
            toastr.error(msg, title, toastr.options);
            break;

        default:
            toastr.info(msg, title, toastr.options);
            break;
    }
}

function UserNameValidation(str) {
    var error = '';
    var illegalChars = /\W/; // allow letters, numbers, and underscores

    if (str == "") {
        error = "Please enter Username.";
    } else if ((str.length < 6) || (str.length > 20)) {
        error = "Username must have 6-20 characters.";
    } else if (illegalChars.test(str)) {
        error = "Please enter valid user name. Use only numbers and alphabets.";
    } else {
        error = "";
    }

    return error;
}

function EmailValidation(email) {
    var pattern = /^\b[A-Z0-9._%-]+@[A-Z0-9.-]+\.[A-Z]{2,4}\b$/i;

    if (!pattern.test(email)) {
        return 'Invalid email address!';
    } else {
        return 'Valid';
    }
}

function PasswordStrength(val) {
    if (val !== '') {
        var number = /([0-9])/;
        var alphabets = /([a-zA-Z])/;
        var special_characters = /([~,!,@,#,$,%,^,&,*,-,_,+,=,?,>,<])/;

        if (val.length < 8) {
            jQuery('#pwd-invalid-feedback').removeClass();
            jQuery('#pwd-invalid-feedback').addClass('text-warning');
            jQuery('#pwd-invalid-feedback').html("Weak (should be atleast 8 characters.)");
        } else {
            if (val.match(number) && val.match(alphabets) && val.match(special_characters)) {
                jQuery('#pwd-invalid-feedback').removeClass();
                jQuery('#pwd-invalid-feedback').addClass('text-success');
                jQuery('#pwd-invalid-feedback').html("Strong");
            } else {
                jQuery('#pwd-invalid-feedback').removeClass();
                jQuery('#pwd-invalid-feedback').addClass('text-primary');
                jQuery('#pwd-invalid-feedback').html("Medium (should include alphabets, numbers and special characters.)");
            }
        }
    } else {
        jQuery('#pwd-invalid-feedback').removeClass();
        jQuery('#pwd-invalid-feedback').html("");
    }
}

var numbersB2E = {
    '০': 0,
    '১': 1,
    '২': 2,
    '৩': 3,
    '৪': 4,
    '৫': 5,
    '৬': 6,
    '৭': 7,
    '৮': 8,
    '৯': 9
};

function NumbersB2E(input) {
    var output = [];
    for (var i = 0; i < input.length; ++i) {
        if (numbersB2E.hasOwnProperty(input[i])) {
            output.push(numbersB2E[input[i]]);
        } else {
            output.push(input[i]);
        }
    }
    return output.join('');
}